package com.example.circlea;

import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.app.AppCompatDelegate;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.content.Intent;
import android.database.Cursor;
import android.os.Bundle;
import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.LinearLayout;
import android.widget.TextView;

import com.google.android.material.floatingactionbutton.FloatingActionButton;

import java.util.ArrayList;

public class HomeActivity extends AppCompatActivity {
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_home);
        UserDB userDB=new UserDB(this);
        TextView welcomeText= (TextView) findViewById(R.id.textView);
        Cursor cursor2 = userDB.fetchAllUsers();
        for(int i=0;i<cursor2.getCount();i++){
            if(cursor2.getString(0).equals(User.getEmail2())){
                welcomeText.setText("Hello, "+cursor2.getString(2));
                break;
            }
            cursor2.moveToNext();
        }
        ArrayList<Categories> categories=new ArrayList<>();
        categories.add(new Categories("Burgers","data:image/jpeg;base64,"));
        categories.add(new Categories("Pizza","data:image/jpeg;base64,"));
        categories.add(new Categories("Pasta","data:image/jpeg;base64,"));

        CategoriesAdapter categoriesAdapter=new CategoriesAdapter(this);
        categoriesAdapter.setCategories(categories);
        RecyclerView categoryrecycleyview=findViewById(R.id.home_hor_rec);
        categoryrecycleyview.setAdapter(categoriesAdapter);
        categoryrecycleyview.setLayoutManager(new LinearLayoutManager(this,LinearLayoutManager.HORIZONTAL,false));

        ArrayList<Product> prods=new ArrayList<>();

        prods.add(new Product("chesse burger","data:image/jpeg;base64,"
                ,"60","burger"));
        prods.add(new Product("Chicken Ranch Pizza","data:image/jpeg;base64,",
        "80","pizza"));
        prods.add(new Product("Fettuccine Alfredo","data:image/jpeg;base64,","80","Pasta"));

        ProductAdaptor productAdaptor=new ProductAdaptor(this);
        productAdaptor.setProducts(prods);
        RecyclerView popRecyclerview=findViewById(R.id.recycle2);
        popRecyclerview.setAdapter(productAdaptor);
        popRecyclerview.setLayoutManager(new LinearLayoutManager(this,LinearLayoutManager.HORIZONTAL,false));

        FloatingActionButton cartBtn = findViewById(R.id.cart_floating_btn);
        cartBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent i = new Intent(HomeActivity.this, CartActivity.class);
                startActivity(i);
            }
        });

        LinearLayout SettingsBtn;
        SettingsBtn = findViewById(R.id.settingsBtn);
        SettingsBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent intent = new Intent(HomeActivity.this,SettingsActivity.class);
                startActivity(intent);
            }
        });
        LinearLayout homebtn;
        homebtn = findViewById(R.id.homeBtn);
        homebtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent intent = new Intent(HomeActivity.this,HomeActivity.class);
                startActivity(intent);
            }
        });
        LinearLayout ProfileBtn;
        ProfileBtn = findViewById(R.id.profileBtn);
        ProfileBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent intent = new Intent(HomeActivity.this,ProfileActivity.class);
                startActivity(intent);
            }
        });
        LinearLayout menuBTN;
        menuBTN = findViewById(R.id.menuBtn);
        menuBTN.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent intent = new Intent(HomeActivity.this,category.class);
                intent.putExtra("category","menu");
                startActivity(intent);
            }
        });
    }
}